import axios from 'axios';
import EditorDemo from "@/components/EditorDemo.vue";
export default {
  components: {
    EditorDemo
  },
  data() {
    return {
      editor: null,
      //服务端地址
      serverUrl: 'http://localhost/post'
    };
  },
  methods: {
    //初始化前
    onAfterInit: function (e) {
      this.editor = e.editor;
    },
    //仅保存HTML文档
    saveHtml: function () {
      let data = {
        'doc': this.editor.getHtml()
      };
      axios.post(this.serverUrl, data).then(res => {
        console.log('res=>', res);
      });
    },
    //保存文档及机构化数据
    saveHtmlAndData: function () {
      let data = {
        'doc': this.editor.getHtml(),
        'data': this.editor.getBindObject()
      };
      axios.post(this.serverUrl, data).then(res => {
        console.log('res=>', res);
      });
    }
  }
};
