mapper.select:
SELECT
orderno
FROM
orderr
where deletetime = 0

    and statuss = #{params.statuss}


    and orderno = #{params.orderno}


    and userid = #{params.userid}


    and shopid = #{params.shopid}

group by orderno

SELECT * FROM orderr where deletetime = 0
and orderno = #{orderno}
ORDER BY id DESC

mapper.update:
update orderr
set deletetime = #{deletetime}
where id in

    #{id}
